import './Weather.css';




const Weather = (props) => {

    const {name, temp} = props.data;

  return(

      <div>
          <div className="WeatherList-item">
              <h2>{name}</h2>
              <h2>{temp + " °C"}</h2>
          </div>
          <div>
              <input type = "Button" value = 'удалить' onClick={props.deleteItem} />
          </div>
      </div>

    );

};


