import React, {Component} from 'react';
import './Weather.css';


const Weather = (props) => {
  return(
      <div>
          <div className="WeatherList-item">
              <h2>{props.data.name}</h2>
              <h2>{props.data.temp}</h2>
              <h3>{props.key}</h3>
          </div>
          <div>
              <input type = "Button" value = 'удалить' onClick={props.deleteItem(props.data.id)}/>
          </div>
      </div>

    );

};


class WeatherList extends Component {


    state = {
        data: this.props.data
    };


    deleteCity = (id) => {

        const newData = [...this.state.data];
        newData.filter(data => data.id !== id);
        this.setState({data: []});

    };


    render() {

        const data = [...this.props.data];
        const weatherList = data.map((data) =>

            <Weather data={data} key={data.id} deleteItem = {() => this.deleteCity(data.id)} />

        );

        return (

            <div className = "WeatherList">{weatherList}</div>

        );
    }

}


export default WeatherList;
