import { ADD_CITY } from "../constants/cityConstants";
import { DELETE_CITY } from "../constants/cityConstants";


export const addCity = (stateCity = [], action) => {

        if (action.type === ADD_CITY) return stateCity = [...stateCity, action.payload];

        else if (action.type === DELETE_CITY) return stateCity.filter(city => city.id !== action.id);

        else return stateCity;


};
